import { Component } from '@angular/core';
import { AuthService } from '../../services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {
  user = {
    name: '',
    tipoDocumento: '',
    numDocumento: 0,
    password: '',
  };

  constructor(private authService: AuthService, private router: Router) {}

  userLogin() {
    this.authService.userLogin(this.user).subscribe(
      (res) => {
        console.log(res);
        localStorage.setItem('token', res.token);
        this.router.navigate(['/dashboard']);
      },
      (err) => {
        console.log(err);
      }
    );
  }
}
<div class="contenedor-general">
  <div class="contenedor-izquierdo">
    <div class="contenedor-imagen"></div>
    <div class="texto-izquierdo-1">Miles de usuarios ya nos eligen</div>

    <div class="texto-izquierdo-2">¡Es tu turno!</div>
  </div>
  <div class="contenedor-derecho">
    <div class="texto-derecho">Crea tu cuenta</div>
    <div class="contenedor-formulario">
      <form (submit)="userLogin()">
        <div class="selectores">
          <label class="labels">Tipo de documento:</label>

          <select
            name="tipoDocumento"
            [(ngModel)]="user.tipoDocumento"
            class="selector-input"
            placeholder="DNI"
          >
            <option value="DNI">DNI</option>
            <option value="Pasaporte" selected>Pasaporte</option>
          </select>
        </div>

        <div class="selectores">
          <label class="labels">Numero de documento:</label>
          <input
            type="number"
            name="numDocumento"
            [(ngModel)]="user.numDocumento"
            class="selector-input"
            placeholder="20123456"
          />
        </div>

        <div class="selectores">
          <label class="labels">Contraseña:</label>
          <input
            type="password"
            name="password"
            [(ngModel)]="user.password"
            class="selector-input"
            placeholder="********"
          />
        </div>

        <button type="submit" class="boton-submit">Crear cuenta</button>
      </form>
    </div>
    <a routerLink="/register" class="link"
      >¿No tienes cuenta? <b>Registrate</b></a
    >
  </div>
</div>
