import { Component } from '@angular/core';
import { AuthService } from '../../services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css'],
})
export class RegisterComponent {
  user = {
    name: '',
    tipoDocumento: '',
    numDocumento: 0,
    password: '',
  };

  constructor(private authService: AuthService, private router: Router) {}

  userRegister() {
    this.authService.userRegister(this.user).subscribe(
      (res) => {
        console.log(res);
        localStorage.setItem('token', res.token);
        this.router.navigate(['/dashboard']);
      },
      (err) => {
        console.log(err);
      }
    );
  }
}
<div class="contenedor-general">
  <div class="contenedor-izquierdo">
    <div class="contenedor-imagen">
      <div class="texto-izquierdo">
        Miles de usuarios ya nos eligen ¡Es tu turno!
      </div>
    </div>
  </div>
  <div class="contenedor-derecho">
    <div class="contenedor-formulario">
      <form (submit)="userRegister()">
        <div class="text-derecho">Crea tu cuenta</div>
        <div>
          <label>Nombre:</label>
          <input type="text" name="name" [(ngModel)]="user.name" />
        </div>

        <div>
          <label>Tipo de documento:</label>

          <select name="tipoDocumento" [(ngModel)]="user.tipoDocumento">
            <option value="DNI">DNI</option>
            <option value="Pasaporte" selected>Pasaporte</option>
          </select>
        </div>

        <div>
          <label>Numero de documento:</label>
          <input
            type="number"
            name="numDocumento"
            [(ngModel)]="user.numDocumento"
          />
        </div>

        <div>
          <label>Contraseña:</label>
          <input type="password" name="password" [(ngModel)]="user.password" />
        </div>

        <button type="submit">Registrarme</button>
      </form>
    </div>

    <a routerLink="/login">Ir al login</a>
  </div>
</div>
