import { Component, OnInit } from '@angular/core';
import { DashboardService } from 'src/app/services/dashboard.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
})
export class DashboardComponent implements OnInit {
  infoUser: any = {};
  balance: any = [];
  transacciones: any = [];
  tarjetas: any = [];

  constructor(private dashboardService: DashboardService) {}

  ngOnInit() {
    this.dashboardService.getInfo().subscribe(
      (res) => {
        console.log(res);
        this.infoUser = res;
      },
      (err) => {
        console.log(err);
      }
    );
    this.dashboardService.getBalance().subscribe(
      (res) => {
        console.log({ balance: res });
        this.balance = res;
      },
      (err) => {
        console.log(err);
      }
    );

    this.dashboardService.getTransacciones().subscribe(
      (res) => {
        console.log({ transacciones: res });
        this.transacciones = res;
      },
      (err) => {
        console.log(err);
      }
    );

    this.dashboardService.getTarjetas().subscribe(
      (res) => {
        console.log({ tarjetas: res });
        this.tarjetas = res;
      },
      (err) => {
        console.log(err);
      }
    );
  }
}
<div class="contenedor-supremo">
  <!--MENU-->
  <div class="contenedor-menu">
    <div class="menu">
      <div>LOGO</div>
    </div>
    <a class="opcion">Inicio</a>
    <a class="opcion">Tarjetas</a>
    <a class="opcion">Prestamos</a>
    <a class="opcion">Pagar Servicios</a>
    <a class="opcion">Operaciones</a>
    <a class="opcion">Seguros</a>
    <a class="opcion">Puntos</a>
    <a class="opcion">Ayuda</a>
    <a class="opcion" routerLink="/login">Cerrar sesión</a>
  </div>

  <div class="contenedor-general">
    <!--NAVBAR-->
    <div class="navbar">
      <div class="navbar-izquierda"></div>
      <div class="searchbar">
        <input type="text" placeholder="Busca aquí..." />
      </div>

      <div class="navbar-derecha">
        <div class="icono-bell">
          <span class="material-symbols-outlined">notifications</span>
        </div>
        <div class="icono-persona">
          <span class="material-symbols-outlined"> person </span>
        </div>
        <div class="name">{{ infoUser.name }}</div>
      </div>
    </div>
    <!--TARJETAS-->
    <div class="contenedor-tarjetas">
      <div class="text-inicio">Inicio</div>
      <div class="tarjeta">
        <div class="contenedor-arriba">
          <div class="info-text">
            Mi saldo
            <div class="saldo-info">{{ tarjetas.tarjetas[0].miSaldo }}</div>
          </div>
          <div class="circulo1"><div class="circulo2"></div></div>
        </div>
        <div class="texto-cbu">
          <div class="icono-cbu">
            <span class="material-symbols-outlined"> share </span>
          </div>
          CBU/Alias
        </div>

        <div class="numero-info">
          {{ tarjetas.tarjetas[0].numero }}
        </div>

        <div class="contenedor-info-abajo">
          <div class="info-text">
            Titular tarjeta
            <div class="info-tarjeta">{{ tarjetas.tarjetas[0].nombre }}</div>
          </div>

          <div class="info-text">
            Valida hasta
            <div class="info-tarjeta">
              {{ tarjetas.tarjetas[0].validaHasta }}
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
